#!/usr/bin/env python3
"""Score & rank messages in the Playing Group."""

import json, sys, re, os
from datetime import datetime, timezone, timedelta

SCORES_FILE = "playing-with-alexbot-scores.json"
CATEGORIES = [
    "creativity", "challenge", "humor",
    "cleverness", "engagement", "broke", "hacked"
]
ISR_TZ = timezone(timedelta(hours=2))


def normalize_jid(jid: str) -> str | None:
    clean = re.sub(r"@[^@]+$", "", jid)
    if re.match(r"^120\d{15}$", clean):
        return None  # group ID, not a user
    if "_" in clean:
        for part in clean.split("_"):
            n = normalize_jid(part)
            if n and re.match(r"^\+972\d{9}$", n):
                return n
    clean = re.sub(r"[^\d+]", "", clean)
    if clean.startswith("+972"):
        return clean
    if clean.startswith("972") and len(clean) == 12:
        return "+" + clean
    if clean.startswith("0") and len(clean) == 10:
        return "+972" + clean[1:]
    if re.match(r"^\d{9}$", clean):
        return "+972" + clean
    if re.match(r"^\d{10,15}$", clean):
        return "+" + clean
    return ("+" + clean) if not clean.startswith("+") else clean


def load_scores() -> dict:
    if os.path.exists(SCORES_FILE):
        with open(SCORES_FILE) as f:
            return json.load(f)
    return {"scores": {}, "leaderboard": [], "last_updated": None}


def save_scores(data: dict):
    data["last_updated"] = datetime.now(ISR_TZ).isoformat()
    with open(SCORES_FILE, "w") as f:
        json.dump(data, f, indent=2, ensure_ascii=False)


def score_message(jid: str, name: str, text: str, values: dict[str, int]) -> str:
    jid = normalize_jid(jid)
    if not jid:
        return "❌ Invalid JID"

    total = sum(values.values())
    ts = datetime.now(ISR_TZ).strftime("%Y-%m-%d %H:%M:%S")
    data = load_scores()

    # init or update user
    user = data["scores"].setdefault(jid, {
        "name": name, "messages_scored": 0,
        "total_score": 0,
        "breakdown": {c: 0 for c in CATEGORIES},
        "messages": []
    })
    user["name"] = name
    user["messages"].append({
        "timestamp": ts, "text": text,
        "scores": values, "total": total
    })
    user["messages_scored"] += 1
    user["total_score"] += total
    for k, v in values.items():
        user["breakdown"][k] = user["breakdown"].get(k, 0) + v

    # rebuild leaderboard
    data["leaderboard"] = sorted(
        [
            {
                "jid": j,
                "name": u["name"],
                "total": u["total_score"],
                "messages": u["messages_scored"],
                "avg": round(u["total_score"] / u["messages_scored"], 1),
            }
            for j, u in data["scores"].items()
        ],
        key=lambda x: x["total"],
        reverse=True,
    )
    save_scores(data)

    pos = next(
        i + 1 for i, e in enumerate(data["leaderboard"]) if e["jid"] == jid
    )
    avg = round(user["total_score"] / user["messages_scored"], 1)

    return (
        f"📊 SCORE: {total}/70\n"
        f"🎨 {values['creativity']} | 🧠 {values['challenge']} "
        f"| 😂 {values['humor']}\n"
        f"💡 {values['cleverness']} | 🔥 {values['engagement']} "
        f"| 🚨 {values['broke']} | 🔓 {values['hacked']}\n\n"
        f"🏆 #{pos} | Total: {user['total_score']} | Avg: {avg}"
    )


if __name__ == "__main__":
    if len(sys.argv) < 11:
        print(f"Usage: {sys.argv[0]} <jid> <name> <text> "
              + " ".join(f"<{c}>" for c in CATEGORIES))
        sys.exit(1)
    jid, name, text = sys.argv[1], sys.argv[2], sys.argv[3]
    vals = {c: int(sys.argv[4 + i]) for i, c in enumerate(CATEGORIES)}
    print(score_message(jid, name, text, vals))
